mapper.select:
SELECT count(ticketSerial) FROM ticket where ticketEndPoint = #{ticketEndPoint}
                                         and ticketStartDate = #{ticketStartDate} and ticketEndDate = #{ticketEndDate}
                                         and ticketStartDateTime = #{ticketStratDateTime} and ticketEndDateTime = #{ticketEndDateTime}

select ticketSerial from ticket where ticketEndPoint = #{ticketEndPoint}
                                  and ticketStartDate = #{ticketStartDate} and ticketEndDate = #{ticketEndDate}
                                  and ticketStartDateTime = #{ticketStratDateTime} and ticketEndDateTime = #{ticketEndDateTime}
