mapper.update:
UPDATE plan SET planNote = #{planNote} 
WHERE planOrder = #{planOrder} AND planDate = #{planDate} AND planbucketSerial = 
(SELECT planbucketSerial FROM planbucket WHERE purchaseSerial = #{purchaseSerial} AND planbucketSerial=#{planbucketSerial})

UPDATE plan SET planOrder = planOrder - 1 WHERE planDate = #{planDate} 
										  AND planOrder > #{prePlanOrder} 
										  AND purchaseSerial = #{purchaseSerial}

UPDATE plan SET 


		planOrder = planOrder - 1 WHERE planOrder  <=  #{planOrder}


		planOrder = planOrder + 1 WHERE planOrder  >=  #{planOrder}


AND planDate = #{planDate} AND purchaseSerial = #{purchaseSerial}

UPDATE plan SET planOrder = planOrder - 1 WHERE planDate = #{planDate} 
										    AND planOrder > #{planOrder} 
											AND purchaseSerial = #{purchaseSerial}
